import React from "react";

import { Link, Element, animateScroll as scroll } from "react-scroll";
import { Link as RouterLink, useLocation, useNavigate } from "react-router-dom";

import { InfoChip, ChipTitle, ChipContent, ChipButton } from "../InfoChip";

import ILink from "../Link";

import { AcmAtSdsu } from "../Modules";
import { HackLogo } from "../Modules";

let chips = [
    // {
    //     date: "March 19",
    //     weekday: "Wednesday",
    //     event: "Registration for the Hackathon began.",
    //     icon: require("../../images/hack25/register_final.jpeg"),
    // },
    {
        date: "April 18",
        weekday: "Friday",
        event: "Last day to register for the Hackathon.",
        icon: require("../../images/hack25/lastday_final.jpeg"),
    },
    {
        date: "April 19",
        weekday: "Saturday",
        event: "Project proposal submissions open.",
        icon: require("../../images/hack25/proposal_final.jpeg"),
    },
    {
        date: "April 25",
        weekday: "Friday",
        event: "Final deadline to submit project proposal.",
        icon: require("../../images/hack25/projectdue_final.jpeg"),
    },
    {
        date: "April 26",
        weekday: "Saturday",
        event: "The Hackathon will begin promptly at 9:00am.",
        icon: require("../../images/hack25/hackathon_final.jpeg"),
    },
];

export default function Timeline({headerStyle}){
    return (
        <section
            id="timeline"
            className="pt-10 pb-20 bg-gradient-to-b from-acm-blue via-blue-900 to-gray-900 text-white"
        >
            <div className="max-w-5xl mx-auto px-4">
                <h1
                    className={`hackheader text-4xl md:text-5xl font-extrabold text-center drop-shadow-lg ${headerStyle || ""}`}
                >
                    Hackathon Timeline
                </h1>
                <p className="text-center text-xl md:text-2xl mt-6 mb-10 p-2 text-gray-200">
                    Stay on track with all the important dates and deadlines for the Hackathon!
                </p>
                <div className="flex flex-wrap gap-6 justify-center">
                    {chips.map((chip, idx) => (
                        <div
                            key={chip.date}
                            className="relative group transition-transform duration-300 hover:scale-105"
                            style={{ flex: "1 1 220px", minWidth: 220, maxWidth: 320 }}
                        >
                            <InfoChip
                                bgcolor="bg-white bg-opacity-90 backdrop-blur-md shadow-xl"
                                textcolor="text-gray-900"
                                otherstyle="rounded-2xl h-full flex flex-col items-center p-4 border-2 border-acm-blue"
                            >
                                <div className="flex flex-col items-center mb-2">
                                    <ChipTitle otherstyle="text-acm-blue text-sm md:text-base font-semibold uppercase tracking-wide">
                                        {chip.weekday}
                                    </ChipTitle>
                                    <ChipTitle otherstyle="text-2xl md:text-3xl font-bold mt-1 mb-2">
                                        {chip.date}
                                    </ChipTitle>
                                </div>
                                <ChipContent>
                                    <p className="text-center text-base md:text-lg font-medium mb-3">
                                        {chip.event}
                                    </p>
                                    <div className="flex justify-center items-center">
                                        <img
                                            src={chip.icon}
                                            alt={chip.event}
                                            className="rounded-xl shadow-md min-w-[70%] max-w-[90%] max-h-[28vh] mx-auto transition duration-300 group-hover:shadow-2xl"
                                            loading="lazy"
                                        />
                                    </div>
                                    <p className="text-xs text-center text-gray-400 mt-2">
                                        img credit: Isabella King
                                    </p>
                                </ChipContent>
                                {/* Example: Add a button for future actions */}
                                {/* <ChipButton
                                    bgcolor="bg-acm-blue"
                                    hovercolor="acm-orange"
                                    goal="register"
                                    otherstyle="mt-4"
                                >
                                    Register
                                </ChipButton> */}
                            </InfoChip>
                            {/* Timeline connector (vertical line) */}
                            {idx < chips.length - 1 && (
                                <span className="absolute left-1/2 -bottom-4 w-1 h-8 bg-acm-blue opacity-60 rounded-full z-0" style={{ transform: "translateX(-50%)" }}></span>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}
